UPDATE reference SET description = 'Le SpinAgain est une tour d’empilage avec des engrenages multicolores tournoyant autour de leur base, rapidement et en douceur. Le SpinAgain développe la motricité fine, la sensorialité, et les compétences de séquençage tout en appréciant le mouvement tourbillonnaire des disques colorés.Hauteur 42 cm. En plastique.' WHERE id = 1799;
