UPDATE reference SET description = 'Edition 1995.Première partie: textes et activités de lecture, vocabulaire, production d ''écrits, poésies;Seconde partie: activités structurales, grammaire, conjugaison, vocabulaire, orthographe.' WHERE id = 1839;
UPDATE reference SET description = 'Edition 1997.Formes, emplois des verbes et tableaux de conjugaison.' WHERE id = 1840;
UPDATE reference SET description = 'Edition 2013.Manuel organisé en 4 domaines : grammaire, conjugaison, orthographe et vocabulaire; des leçons axées sur les attendus de fin de cycle et plus de 1 000 exercices classés par compétences.' WHERE id = 1841;
UPDATE reference SET description = 'Edition 2010.Manuel organisé en 3 partes: grammaire/conjugaison, orthographe et vocabulaire. Chacun de ces domaines propose un apprentissage des notions par la découverte de la notion (recherche, construction de la règle, jeu oral et évaluation), un entrainement différencié et un réinvestissement en lecture et en écriture.' WHERE id = 1842;
